import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router';
import { NavLink } from 'react-router-dom';
import { Skeleton } from 'react-loading-skeleton';
const Product = () => {

    const { id } = useParams();
    const [product, setProduct] = useState([]);
    const [loading, setLoading] = useState(false);
    let isMounted = true;


    useEffect(() => {
        const getProduct = async () => {
            setLoading(true);
            const response = await fetch(`https://fakestoreapi.com/Product/${id}`);
            if (isMounted) {

                setProduct(await response.json());
                setLoading(false);
                // console.log(filter);
            }
        }
        getProduct();
    }, [input]);



    function Loading() {
        return (
            <>
                <div className="col-md-6">
                 <Skeleton height={400} />

                    </div>
              <div className="col-md-6" style={{lineHeight:2}}>
                    <Skeleton height={50} width={300} />
                
                    <Skeleton height={75} />
                    <Skeleton height={25} width={150} />

                    <Skeleton height={50} />
                    <Skeleton height={150} />
                    <Skeleton height={50} width={100} />
                    <Skeleton height={50} width={100}  style={{marginLeft:6}} />




                </div>            
            </>
        );
    };


    const ShowProduct = () => {
        return (
            <>
                <div className='col-md-6'>
                    <img src={product.image} alt={product.title} height="400px" width="400px" />
                </div>
                <div className="col-md-6">
                    <h4 className="text-uppercase text-black-50" onClick={() => setFilter(data)}>{product.category}</h4>
                    <p className="lead fw-bold" onClick={() => filterProduct("Watches")}>Rating {product.rating && product.rating.rate}
                        <i className='fa fa-star'></i>
                    </p>

                    <h3 className="display-6 fw-bold my-4" onClick={() => filterProduct("Laptops")}>${product.price}</h3>
                    <p className="lead" onClick={() => filterProduct("Watches")}>{product.description}</p>
                    <NavLink to="/cart" className="btn btn-outline-dark me-2" onClick={() => filterProduct("Electronics")}>Add to Cart</NavLink>
                    <NavLink className="btn btn-dark me-2 py-4" onClick={() => filterProduct("Screens")}>Go to Cart</NavLink>
                </div>
                {filter.map((product) => {
                    return (
                        <>
                            <div className="col-md-3 mb-4">
                                <div className="card h-100 text-center p-4" key={product.id}>
                                    <img className="card-img-top" src={product.img} alt={product.title} height="250px" />
                                    <div className="card-body">
                                        <h5 className="card-title mb-0">{product.title.substring(0, 12)}</h5>
                                        <p className="card-text lead fw-bold">${product.price}</p>
                                        <NavLink href="#" className="btn btn-outline-dark">Buy Now</NavLink>
                                    </div>
                                </div>
                            </div>
                        </>
                    );
                })}


            </>

        );
    }
    return (
        <div>
            <div className="container  py-5">
               
                <div className="row">
                    {loading ? <Loading/> : <ShowProduct/>}
                </div>
            </div>
        </div>
    );
}


export default Product;
