import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router';
import { NavLink } from 'react-router-dom';
import { Skeleton } from "react-loading-skeleton/dist/skeleton.css";;
import { useDispatch } from 'react-redux';
import { addCart } from '../redux/action';

const Product = () => {
    const { id } = useParams();
    const [product, setProduct] = useState(null); // Initialize as null
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null); // Add error state
    const dispatch = useDispatch();

    const addProduct = (product) => {
        dispatch(addCart(product));
    };

    useEffect(() => {
        const getProduct = async () => {
            setLoading(true);
            try {
                const response = await fetch(`https://fakestoreapi.com/products/${id}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch product');
                }
                const data = await response.json();
                setProduct(data);
            } catch (error) {
                setError(error.message);
            }
            setLoading(false);
        };
        
        getProduct();
    }, [id]);

    if (error) {
        return <div>Error: {error}</div>;
    }

    const Loading = () => (
        <>
            <div className="col-md-6">
                <Skeleton height={400} />
            </div>
            <div className="col-md-6" style={{ lineHeight: 2 }}>
                <Skeleton count={5} />
            </div>
        </>
    );

    const ShowProduct = () => (
        <>
            <div className='col-md-6'>
                <img src={product.image} alt={product.title} height="400px" width="400px" />
            </div>
            <div className="col-md-6">
                <h4 className="text-uppercase text-black-50">{product.category}</h4>
                <p className="lead fw-bold">Rating {product.rating && product.rating.rate} <i className='fa fa-star'></i></p>
                <h3 className="display-6 fw-bold my-4">${product.price}</h3>
                <p className="lead">{product.description}</p>
                <button className="btn btn-outline-dark px-4 py-2" onClick={() => addProduct(product)}>Add to Cart</button>
                <NavLink className="btn btn-dark ms-2 px-3 py-2" to="/cart">Go to Cart</NavLink>
            </div>
        </>
    );

    return (
        <div>
            <div className="container py-5">
                <div className="row">
                    {loading ? <Loading /> : (product ? <ShowProduct /> : <div>No product found</div>)}
                </div>
            </div>
        </div>
    );
};

export default Product;
